import { Link } from "react-router-dom";

const RobotDetails = ({ robot }) => {
    if (!robot) {
        return <div>Loading...</div>;
    }

    const { id, name, image, description, price, rating } = robot;

    return (
        <div className="container mx-auto py-6 px-4">
            <div className="flex flex-wrap -mx-4">
                <div className="w-full lg:w-1/2 px-4 mb-6 lg:mb-0">
                    <img src={image} alt={name} className="rounded shadow-md" />
                </div>
                <div className="w-full lg:w-1/2 px-4">
                    <h2 className="text-2xl font-bold mb-2">{name}</h2>
                    <p className="text-gray-600 text-sm mb-4">{description}</p>
                    <div className="mb-4">
                        <span className="text-gray-700 font-bold mr-2">${price}</span>
                        <span className="text-gray-600 text-sm">(Free Shipping)</span>
                    </div>
                    <div className="mb-4">
                        <span className="text-gray-700 font-bold mr-2">{rating}</span>
                        <span className="text-gray-600 text-sm">stars</span>
                    </div>
                    <Link
                        to="/robots"
                        className="bg-gray-900 text-white py-2 px-4 rounded hover:bg-gray-800"
                    >
                        Back to Robots
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default RobotDetails;
