import { useParams } from 'react-router-dom';

function RobotDetails({ robot }) {
    const { id } = useParams();
    const selectedRobot = robot.find(robot => robot.id === Number(id));

    return (
        <div>
            <h2>{selectedRobot.name}</h2>
            <p>Model: {selectedRobot.model}</p>
            <p>Price: ${selectedRobot.price}</p>
            <p>Status: {selectedRobot.status}</p>
            <p>Description: {selectedRobot.description}</p>
            <img src={selectedRobot.image} alt={selectedRobot.name} />
        </div>
    );
}

export default RobotDetails;
