import { Link } from "react-router-dom";

const RobotDetails = ({ robot }) => {
    if (!robot) {
        return <div>Loading...</div>;
    }

    const { id, name, model, manufacturer, serialNumber, height, mass, status, description } = robot;

    return (
        <div className="flex justify-center">
            <div className="w-full max-w-xs">
                <div className="bg-white shadow-md rounded-lg p-6">
                    <div className="block relative h-48 rounded overflow-hidden">
                        <img
                            alt="robot"
                            src={image}
                            className="w-full h-full object-cover"
                        />
                    </div>
                    <div className="mt-4">
                        <h2 className="text-gray-700 font-semibold text-lg">{name}</h2>
                        <p className="mt-2 text-gray-600 text-sm">{id}</p>
                        <p className="mt-2 text-gray-600 text-sm">{username}</p>
                        <p className="mt-2 text-gray-600 text-sm">{phone}</p>
                        <p className="mt-2 text-gray-600 text-sm">{website}</p>
                        <p className="mt-2 text-gray-600 text-sm">
                            {address.city}, {address.street}, {address.suite}
                        </p>
                        <p className="mt-2 text-gray-600 text-sm">
                            {company.name}, {company.catchPhrase}
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default RobotDetails;
