import { Link } from "react-router-dom";

const RobotDetails = ({ robot }) => {
    if (!robot) {
        return <div>Loading...</div>;
    }

    const { id, name, model, manufacturer, serialNumber, height, mass, status, description, sensors } = robot;

    const Sensor = ({ type, status }) => (
        <div className="flex justify-between py-1">
            <p>{type}</p>
            <p className={`ml-2 ${status ? 'text-green-600' : 'text-red-600'}`}>{status ? 'OK' : 'FAILED'}</p>
        </div>
    );

    const Sensors = ({ sensors }) => (
        <div className="bg-gray-100 rounded-md shadow-lg p-4">
            <h2 className="font-bold mb-4">Sensors</h2>
            {/* <div>
                {sensors.map((sensor) => (
                    <Sensor key={sensor.type} type={sensor.type} status={sensor.status} />
                ))}
            </div> */}
            <Link to={`/robots/${id}/feed`} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded-md">
                View Camera Feed
            </Link>
        </div>
    );

    return (
        <div className="max-w-2xl mx-auto mt-8">
            <div className="flex items-center">
                <img className="w-32 h-32 mr-8" src={`https://robohash.org/${id}`} alt={`Robot ${name}`} />
                <div>
                    <h1 className="text-4xl font-bold mb-4">{name}</h1>
                    <p className="text-gray-700 font-semibold mb-1">{model}</p>
                    <p className="text-gray-600 mb-4">{manufacturer}</p>
                    <p className="mb-4">
                        Serial Number: <span className="font-semibold">{serialNumber}</span>
                    </p>
                    <p className="mb-4">
                        Height: <span className="font-semibold">{height} cm</span>
                    </p>
                    <p className="mb-4">
                        Mass: <span className="font-semibold">{mass} kg</span>
                    </p>
                    <p className={`text-lg font-semibold ${status ? 'text-green-600' : 'text-red-600'}`}>{status ? 'Operational' : 'Out of order'}</p>
                    <p className="text-gray-700 mt-4">{description}</p>
                </div>
            </div>
            <Sensors sensors={sensors} />
        </div>
    );
};

export default RobotDetails;
