import React from "react";
import { Link } from "react-router-dom";

function RobotDetails({ robot, showAdditionalInfo = false }) {
    return (
        <>
            {robot ? (
                <div className="robot-details">
                    <h2>{robot.name}</h2>
                    <p>Robot Name: {robot.username}</p>
                    <p>Email: {robot.email}</p>
                    {showAdditionalInfo && (
                        <>
                            <p>Phone: {robot.phone}</p>
                            <p>Website: {robot.website}</p>
                            <p>
                                Address: {robot.address.street}, {robot.address.city},{" "}
                                {robot.address.zipcode}
                            </p>
                            <p>Company: {robot.company.name}</p>
                        </>
                    )}
                    <Link to="/">Back to List</Link>
                    <br />
                    <Link to={`/robot/${robot.id}/additional-info`}>
                        Show Additional Info
                    </Link>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </>
    );
}

export default RobotDetails;
